```kotlin:app/src/main/java/android/saswat/winmyargument/ui/signInUp/SignIn.kt
package android.saswat.winmyargument.ui.signInUp

import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.foundation.text.KeyboardOptions
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.alpha
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.layout.ContentScale
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.input.KeyboardType
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.text.input.VisualTransformation
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import android.saswat.winmyargument.R

@Composable
fun SignInScreen(
 onSignInClick: () -> Unit = {},
 onSignUpClick: () -> Unit = {}
) {
 val backgroundColor = Color(0xFFD6C69B) // Beige/gold background
 val textColor = Color(0xFF000000) // Black text

 var email by remember { mutableStateOf("") }
 var password by remember { mutableStateOf("") }
 var confirmPassword by remember { mutableStateOf("") }
 var passwordVisible by remember { mutableStateOf(false) }
 var confirmPasswordVisible by remember { mutableStateOf(false) }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(backgroundColor)
 ) {
 // Low opacity badge in background
 Image(
 painter = painterResource(id = R.drawable.badge),
 contentDescription = "Badge",
 modifier = Modifier
 .align(Alignment.Center)
 .size(200.dp)
 .alpha(0.1f),
 contentScale = ContentScale.Fit
 )

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(24.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 Spacer(modifier = Modifier.height(40.dp))

 // Heading
 Text(
 text = "Let's Start your Win\nStreak ",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = textColor,
 textAlign = TextAlign.Start,
 modifier = Modifier.fillMaxWidth()
 )

 Spacer(modifier = Modifier.height(40.dp))

 // Email field
 OutlinedTextField(
 value = email,
 onValueChange = { email = it },
 placeholder = { Text("Enter email") },
 singleLine = true,
 colors = TextFieldDefaults.colors(
 unfocusedContainerColor = Color.Transparent,
 focusedContainerColor = Color.Transparent,
 unfocusedIndicatorColor = textColor,
 focusedIndicatorColor = textColor
 ),
 modifier = Modifier.fillMaxWidth(),
 shape = RoundedCornerShape(24.dp)
 )

 // Password field
 OutlinedTextField(
 value = password,
 onValueChange = { password = it },
 placeholder = { Text("Enter the password") },
 singleLine = true,
 visualTransformation = if (passwordVisible) VisualTransformation.None else PasswordVisualTransformation(),
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Password),
 trailingIcon = {
 IconButton(onClick = { passwordVisible = !passwordVisible }) {
 Icon(
 painter = painterResource(
 id = if (passwordVisible) R.drawable.ic_visibility else R.drawable.ic_visibility_off
 ),
 contentDescription = if (passwordVisible) "Hide password" else "Show password"
 )
 }
 },
 colors = TextFieldDefaults.colors(
 unfocusedContainerColor = Color.Transparent,
 focusedContainerColor = Color.Transparent,
 unfocusedIndicatorColor = textColor,
 focusedIndicatorColor = textColor
 ),
 modifier = Modifier.fillMaxWidth(),
 shape = RoundedCornerShape(24.dp)
 )

 // Confirm Password field
 OutlinedTextField(
 value = confirmPassword,
 onValueChange = { confirmPassword = it },
 placeholder = { Text("Confirm password") },
 singleLine = true,
 visualTransformation = if (confirmPasswordVisible) VisualTransformation.None else PasswordVisualTransformation(),
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Password),
 trailingIcon = {
 IconButton(onClick = { confirmPasswordVisible = !confirmPasswordVisible }) {
 Icon(
 painter = painterResource(
 id = if (confirmPasswordVisible) R.drawable.ic_visibility else R.drawable.ic_visibility_off
 ),
 contentDescription = if (confirmPasswordVisible) "Hide password" else "Show password"
 )
 }
 },
 colors = TextFieldDefaults.colors(
 unfocusedContainerColor = Color.Transparent,
 focusedContainerColor = Color.Transparent,
 unfocusedIndicatorColor = textColor,
 focusedIndicatorColor = textColor
 ),
 modifier = Modifier.fillMaxWidth(),
 shape = RoundedCornerShape(24.dp)
 )

 Divider(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 color = textColor.copy(alpha = 0.5f)
 )

 // Social sign-in options with badge background
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp)
 ) {
 Row(
 horizontalArrangement = Arrangement.SpaceEvenly,
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.fillMaxWidth()
 ) {
 // Google icon
 IconButton(onClick = { /* Google sign-in logic */ }) {
 Image(
 painter = painterResource(id = R.drawable.google_icon),
 contentDescription = "Sign in with Google",
 modifier = Modifier.size(36.dp)
 )
 }

 // GitHub icon
 IconButton(onClick = { /* GitHub sign-in logic */ }) {
 Image(
 painter = painterResource(id = R.drawable.github_icon),
 contentDescription = "Sign in with GitHub",
 modifier = Modifier.size(36.dp)
 )
 }
 }

 // Badge in background with low opacity
 Image(
 painter = painterResource(id = R.drawable.badge),
 contentDescription = null,
 modifier = Modifier
 .fillMaxWidth()
 .height(60.dp)
 .alpha(0.2f),
 contentScale = ContentScale.FillWidth
 )
 }

 Spacer(modifier = Modifier.weight(1f))

 // Already have an account
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Center,
 modifier = Modifier.fillMaxWidth()
 ) {
 Text(
 text = "Already have an account? ",
 color = textColor
 )
 TextButton(onClick = onSignInClick) {
 Text(
 text = "Sign In",
 color = Color(0xFF02A3AF) // Teal color for the link
 )
 }
 }
 }
 }
}
