@Composable
fun SignUpScreen(
 onSignInClick: () -> Unit = {},
 navController: NavController,
 authViewModel: AuthViewModel = viewModel(),
 onNavigateToMain: (Boolean) -> Unit = { success ->
 if (success) {
 navController.navigate(Screens.MainScreen.route) {
 popUpTo("sign_in") { inclusive = true }
 }
 }
