 @Composable
 fun ActionButton(
 iconRes: Int,
 contentDescription: String,
 onClick: () -> Unit
 ) {
 val interactionSource = remember { MutableInteractionSource() }
 val isPressed by interactionSource.collectIsPressedAsState()
 val scale by animateFloatAsState(
 targetValue = if (isPressed) 0.9f else 1f,
 animationSpec = tween(durationMillis = 100)
 )

 Box(
 modifier = Modifier
 .graphicsLayer {
 scaleX = scale
 scaleY = scale
 }
 .clip(CircleShape)
 .border(2.dp, Color.Black, CircleShape)
 .background(Color(0xFFE8D6B3))
 .clickable(
 interactionSource = interactionSource,
 indication = null,
 onClick = onClick
 )
 .padding(16.dp),
 contentAlignment = Alignment.Center
 ) {
 Image(
 painter = painterResource(id = iconRes),
 contentDescription = contentDescription,
 modifier = Modifier.size(45.dp),
 )
 }
 }

 @Composable
 fun ChatButton(onClick: () -> Unit) {
 val interactionSource = remember { MutableInteractionSource() }
 val isPressed by interactionSource.collectIsPressedAsState()
 val scale by animateFloatAsState(
 targetValue = if (isPressed) 0.95f else 1f,
 animationSpec = tween(durationMillis = 100)
 )
 val backgroundColor by animateColorAsState(
 targetValue = if (isPressed) Color(0xFFE0C9A6) else Color(0xFFE8D6B3),
 animationSpec = tween(durationMillis = 100)
 )

 Box(
 modifier = Modifier
 .fillMaxWidth()
 .graphicsLayer {
 scaleX = scale
 scaleY = scale
 }
 .border(2.dp, Color.Black, RoundedCornerShape(8.dp))
 .background(backgroundColor, RoundedCornerShape(8.dp))
 .clickable(
 interactionSource = interactionSource,
 indication = null,
 onClick = onClick
 )
 .padding(16.dp)
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Box(
 modifier = Modifier
 .clip(CircleShape)
 .background(Color(0xFFFF6B6B))
 .border(1.dp, Color.Black, CircleShape)
 .padding(8.dp)
 ) {
 Image(
 painter = painterResource(id = R.drawable.speech_bubble),
 contentDescription = "Chat",
 modifier = Modifier.size(45.dp)
 )
 }
 Text(
 text = "Chat with your friends",
 color = Color.Black,
 fontSize = 23.sp,
 fontFamily = FontFamily(Font(R.font.inriasansbold)),
 modifier = Modifier.wrapContentSize()
 )
 }
 }
 }


