app/src/main/java/android/saswat/winmyargument/navigation/Navigation.kt
```kotlin:app/src/main/java/android/saswat/winmyargument/navigation/Navigation.kt
package android.saswat.winmyargument.navigation



import android.saswat.winmyargument.ui.Screens
import android.saswat.winmyargument.ui.about.AboutScreen
import android.saswat.winmyargument.ui.about.HowScreen
import android.saswat.winmyargument.ui.about.WhyScreen
import android.saswat.winmyargument.ui.mainScreen.MainScreen
import android.saswat.winmyargument.ui.signInUp.SignInScreen
import android.saswat.winmyargument.ui.signInUp.SignUpScreen
import androidx.compose.animation.AnimatedContentTransitionScope
import androidx.compose.animation.core.tween
import androidx.compose.runtime.Composable
import androidx.compose.runtime.getValue
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.currentBackStackEntryAsState
import androidx.navigation.compose.rememberNavController


@Composable
fun Navigation(
 navController: NavHostController
) {

 NavHost(
 navController = navController,
 startDestination = Screens.About.route
 ) {
 composable(
 route = Screens.About.route,
 enterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 exitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 popEnterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 },
 popExitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 }
 ) {
 AboutScreen(
 onNavigateTo = {
 navController.navigate(Screens.Why.route) {
 launchSingleTop = true
 restoreState = true
 }
 },
 navController = navController
 )
 }
 composable(
 route = Screens.Why.route,
 enterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 exitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 popEnterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 },
 popExitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 }
 ) {
 WhyScreen(
 onNavigateTo = {
 navController.navigate(Screens.How.route) {
 launchSingleTop = true
 restoreState = true
 }
 },
 navController = navController,
 )
 }
 composable(
 route = Screens.How.route,
 enterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 exitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Left,
 animationSpec = tween(300)
 )
 },
 popEnterTransition = {
 slideIntoContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 },
 popExitTransition = {
 slideOutOfContainer(
 AnimatedContentTransitionScope.SlideDirection.Right,
 animationSpec = tween(300)
 )
 }
 ) {
 HowScreen(
 navController = navController,
 onNavigateTo = {
 navController.navigate(Screens.SignIn.route) {
 launchSingleTop = true
 restoreState=true
 }
 }
 )
 }

 composable(
 route = Screens.SignIn.route,
 enterTransition = {
