 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFFC2B280)),
 contentAlignment = Alignment.Center
 ) {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center,
 modifier = Modifier
 .fillMaxSize()
 .padding(horizontal = 24.dp, vertical = 48.dp)
 ) {
 AnimatedText("How It Works ?")

 Spacer(modifier = Modifier.height(20.dp))

 Text(
 text = "We analyze scientific papers, expert opinions, and peer-reviewed studies to craft fact-based, balanced arguments. The app presents both sides, helping you make your case with confidence.",
 fontSize = 27.sp,
 textAlign = TextAlign.Center,
 lineHeight = 32.sp,
 modifier = Modifier.padding(horizontal = 16.dp),
 fontFamily = FontFamily(Font(R.font.josefinslabregular))
 )

 Spacer(modifier = Modifier.height(40.dp))

 Image(
 painter = painterResource(id = R.drawable.next),
 contentDescription = "Start",
 modifier = Modifier
 .size(70.dp)
 .clickable { 
 // Mark that onboarding is complete
 val sharedPreferences = context.getSharedPreferences(MainActivity_PREFS, Context.MODE_PRIVATE)
 sharedPreferences.edit().putBoolean(IS_FIRST_LAUNCH, false).apply()
 // Let the MainActivity know we've handled navigation
 sharedPreferences.edit().putBoolean(HAS_RUN_NAVIGATION, true).apply()

 // Navigate to Sign In
 // Log that we're navigating to SignIn
 Log.d("Navigation", "Navigating to SignIn from How screen")
 // Navigate with clearBackStack to avoid conflicting with MainActivity navigation
 navController.navigate(Screens.SignIn.route) {
 popUpTo(0) { inclusive = true }
 }
 }
 )

 Spacer(modifier = Modifier.height(100.dp))
 }
 }
